    public CenteringActivity() : base()
    {
        _activityName = "Centering Activity";

        _activityDescription = "This activity will help center you by refocusing your thoughts and feelings to the Savior. Clear your mind and focus on the screen.";
    }

    public void RunCenteringActivity()
    {
        this.DisplayStartMessage();

        Console.Clear();

        DateTime currentTime = DateTime.Now;
        DateTime endTime = currentTime.AddSeconds(_activityDuration);

        int i = 0;

        while (currentTime < endTime)
        {
            Console.Write(_centeringStrings[i]);
            
            Thread.Sleep(10000);
            
            Console.Clear();
            Console.WriteLine("\x1b[3J");
            Console.Clear();
            
            if (i < 10)
            {
                i++;
            }
            
            else
            {
                i = 0;
            }

            currentTime = DateTime.Now;
        }

        this.DisplayEndMessage();
    }
}